import React, {Component} from 'react';
import ReactDom from 'react-dom';
import './visualizer.css';

export default class visualizer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cellSize: 15,
            rows: 50,
            columns: 40,
            canvasHeight: 15 * 40,
            canvasWidth: 15 * 50
        }

        this.handleMouseClick = this.handleMouseClick.bind(this);
        this.handleMouseHover = this.handleMouseHover.bind(this);
    }

    componentDidMount() {
        this.canvasVis.width = this.state.canvasWidth;
        this.canvasVis.height = this.state.canvasHeight;
        this.getCanvasPosition(this.canvasVis);

        //drawing all cells for canvas
        this.drawMap();
    }

    getCanvasPosition(canvasID) {
        let rect = canvasID.getBoundingClientRect();
        this.setState({ canvasPosition: {left: rect.left, right: rect.right, top: rect.top, bottom: rect.bottom } });
    }

    //method used to draw all the cells for canvas
    drawMap() {

        for(let x = 0; x < this.state.rows; x++){
            let startingX = this.state.cellSize * x;
            for(let y = 0; y < this.state.columns; y++){
                let startingY = this.state.cellSize * y;

                this.drawCell(this.canvasVis,startingX,startingY);
            }
        }
    }

    drawCell(canvasID, startX, startY) {

        this.drawLine(canvasID,{x: startX, y: startY},{x: startX + this.state.cellSize, y: startY});
        this.drawLine(canvasID,{x: startX + this.state.cellSize, y: startY},{x: startX + this.state.cellSize, y: startY + this.state.cellSize});
        this.drawLine(canvasID,{x: startX, y: startY + this.state.cellSize},{x: startX + this.state.cellSize, y: startY + this.state.cellSize});
        this.drawLine(canvasID,{x: startX, y: startY},{x: startX, y: startY + this.state.cellSize});

    }

    drawLine(canvasID, start, end) {
        const ctx = canvasID.getContext("2d");
        ctx.beginPath();
        ctx.moveTo(start.x, start.y);
        ctx.lineTo(end.x,end.y);
        ctx.stroke();
        ctx.closePath();
    }

    //filling cell using top left coordiinates x and y of cell
    fillCell(canvasID, x, y, fillColor) {
        const ctx = canvasID.getContext("2d");
        ctx.beginPath();
        ctx.fillStyle = fillColor;
        ctx.moveTo(x, y);
        ctx.lineTo(x+this.state.cellSize,y);
        ctx.lineTo(x+this.state.cellSize,y + this.state.cellSize);
        ctx.lineTo(x,y + this.state.cellSize);
        ctx.lineTo(x,y);
        ctx.closePath();
        ctx.fill();
    }

    pixelToCell(pixelX,pixelY) {

        let row = Math.floor(pixelX / this.state.cellSize);
        let column = Math.floor(pixelY / this.state.cellSize);

        let cell = {x : row, y: column};

        return cell;

    }

    handleMouseClick(e) {
        const {left, right, top, bottom } = this.state.canvasPosition;
        let offsetX = e.pageX - left;
        let offsetY = e.pageY - top;

        //getting cell which correspondds to where user clicked
        let cellClicked = this.pixelToCell(offsetX,offsetY);

        let x = cellClicked.x * this.state.cellSize;
        let y = cellClicked.y * this.state.cellSize;

        this.fillCell(this.canvasVis,x,y,"#7a7a7a");
    }

    handleMouseHover(e) {

    }

    render() {
        return (
            <div>
                <canvas ref={ canvasVis => this.canvasVis = canvasVis} onMouseDown = {this.handleMouseClick} onMouseMove = {this.handleMouseHover} ></canvas>
            </div>
        )
    }
}
