            if(this.open.length != 0){
                //remove node from queue
                let currentNode = this.open.shift();
                this.executeSearch(currentNode);                  
            }
            else{
                //return error
                //may happen when path is impossible like due to color difference in path 
                //reseting parameters
                this.resetParameters();
            }

        } 
        else{
            this.inProgress = false;
        }

        this.config.actions.forEach(action => {
            let isLegal = this.isLegalAction(currentNode.x, currentNode.y, action);        
            if(isLegal){
                let nx = action[0] + currentNode.x;
                let ny = action[1] + currentNode.y;
                //check to see if solution is found
